        //demo2
        public static void demo2()
        {
            Console.SetWindowSize(120, 40);

            Console.Write("Text to encrypt: ");
            string plainText = Console.ReadLine();
            byte[] state = Conversions.stringToByteArray(Conversions.stringToHex(plainText));

            Console.Write("Encryption password: ");
			string[] keys = KeyMgmt.generateKeys(Console.ReadLine());

            string key1 = Conversions.stringToHex(keys[1]);
			string key2 = Conversions.stringToHex(keys[2]);
			string key3 = Conversions.stringToHex(keys[3]);
			string key4 = Conversions.stringToHex(keys[4]);


            Console.Write("Bytes:     ");
            fastPrintCrypto(state);
            Console.ReadLine();

            state = Crypto.addPadding(state);
            Console.Write("+Padding:  ");
            fastPrintCrypto(state);
            Console.ReadLine();

            state = Crypto.encrypt(state, key1);
            state = Crypto.encrypt(state, key2);
			state = Crypto.encrypt(state, key3);
			state = Crypto.encrypt(state, key4);

            Console.Write("Encrypted: ");
            fastPrintCrypto(state);
            Console.ReadLine();

			state = Crypto.decrypt(state, key4);
			state = Crypto.decrypt(state, key3);
            state = Crypto.decrypt(state, key2);
            state = Crypto.decrypt(state, key1);
            Console.Write("Decrypted: ");
            fastPrintCrypto(state);
            Console.ReadLine();

            state = Crypto.removePadding(state);
            Console.Write("-Padding:  ");
            fastPrintCrypto(state);
            Console.ReadLine();

            Console.WriteLine("Plain text: >" + System.Text.Encoding.Default.GetString(state) + "<");
            Console.ReadLine();
        }
        
        //print array to screen (larger)
        public static void printCrypto(byte[] blocks)
        {
            int x = 1;
            int y = 1;
            Console.Clear();

            foreach (byte b in blocks)
            {
                Console.SetCursorPosition(x, y);
                Console.Write(Conversions.byteToHex(b) + " ");
                x = Console.CursorLeft - 1;

                if (x >= 94)
                {
                    x = 1;
                    y = y + 2;
                }
                else
                {
                    x++;
                }
            }
        }

        //print array (used for one-liners)
        public static void fastPrintCrypto(byte[] blocks, Boolean newLine = true)
        {
            foreach (byte b in blocks)
            {
                Console.Write(Conversions.byteToHex(b) + " ");
            }
			if (newLine)
				Console.WriteLine();

        }
